        private static string DecodeMasa(string str)
        {
            var sb = new StringBuilder();
            var len = str.Length / 3;
            for (int i = 0; i < len; i++)
            {
                var val = int.Parse(str.Substring(i * 3, 3)) - 1;
                sb.Append(Convert.ToChar(val));
            }

            return sb.ToString();
        }

